package org.deltaproject.channelagent.dummy;

public class DummyData {

	public static byte[] hexStringToByteArray(String s) {
		int len = s.length();
		byte[] data = new byte[len / 2];
		for (int i = 0; i < len; i += 2) {
			data[i / 2] = (byte) ((Character.digit(s.charAt(i), 16) << 4) + Character.digit(s.charAt(i + 1), 16));
		}
		return data;
	}

	public static final String featureRes = "";
	public static final String getConfigRes = "0108000cfffffffb0000ffff";
	public static final String statsRes = "";
	public static final String experimenter = "0104001400000001000023200000000b00000001";

}